import React from 'react';
import { useParams } from 'react-router-dom';
import palettes from '../data/palette.json';
import './PalettePage.css';

const PalettePage = () => {
  const { id } = useParams();
  const palette = palettes.find(palette => palette.id === id);

  if (!palette) {
    return <div>Palette not found</div>;
  }

  return (
    <div className="palette-page">
      <h2>{palette.paletteName} {palette.emoji}</h2>
      <div className="color-grid">
        {palette.colors.map((color, index) => (
          <div key={index} className="color" style={{ backgroundColor: color.color }}>
            {color.name}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PalettePage;
