import React, { useState } from 'react';
import { useParams } from 'react-router-dom'; // Removed useNavigate
import palettes from '../data/palette.json';
import './PalettePage.css';
import clickSound from '../assets/click-sound.mp3'; // Import the sound file

const PalettePage = () => {
  const { id } = useParams();
  const palette = palettes.find(palette => palette.id === id);
  const [fullscreenColor, setFullscreenColor] = useState(null);

  if (!palette) {
    return <div>Palette not found</div>;
  }

  const handleColorClick = (color) => {
    // Play the sound
    const audio = new Audio(clickSound);
    audio.play();

    // Copy color to clipboard and show full screen
    navigator.clipboard.writeText(color.color);
    setFullscreenColor(color.color);

    setTimeout(() => {
      setFullscreenColor(null);
    }, 1000);
  };

  return (
    <div className="palette-page">
      <header className="palette-header">
        <a href="/" className="back-button">Back</a>
        <h2>{palette.paletteName} {palette.emoji}</h2>
      </header>
      <div className="color-grid">
        {palette.colors.map((color, index) => (
          <div
            key={index}
            className="color"
            style={{ backgroundColor: color.color }}
            onClick={() => handleColorClick(color)}
          >
            {color.name}
          </div>
        ))}
      </div>
      {fullscreenColor && (
        <div className="fullscreen" style={{ backgroundColor: fullscreenColor }}>
          <div className="fullscreen-content">
            <span className="color-code">{fullscreenColor}</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default PalettePage;
